import Vue from 'vue'
import Router from 'vue-router'
// import Login from './components/Login.vue'
const Login = () => import(/* webpackChunkName:"login_home_welcome" */ './components/Login.vue')
// import Home from './components/Home.vue'
const Home = () => import(/* webpackChunkName:"login_home_welcome" */ './components/Home.vue')

const BookInfo = () => import(/* webpackChunkName:"BookInfo_BookBorrow_UserInfo_Manage" */ './components/Book/BookInfo.vue')
const BookBorrow = () => import(/* webpackChunkName:"BookInfo_BookBorrow_UserInfo_Manage" */ './components/Book/BookBorrow.vue')
const UserInfo = () => import(/* webpackChunkName:"BookInfo_BookBorrow_UserInfo_Manage" */ './components/User/UserInfo.vue')
const userManage = () => import(/* webpackChunkName:"BookInfo_BookBorrow_UserInfo_Manage" */ './components/userManage/userManage.vue')





Vue.use(Router)

const router = new Router({
    routes: [{
        path: '/',
        redirect: 'login'
    },
    {
        path: '/login',
        component: Login
    },
        {
        path: '/home',
        component: Home,
        children: [{
            path: '/home',
            redirect: '/bookInfo'
        },
        {
            path: '/bookInfo',
            component: BookInfo
        },
        {
            path: '/bookBorrow',
            component: BookBorrow
        },
        {
            path: '/userInfo',
            component: UserInfo
        },
        {
            path: '/userManage',
            component: userManage
        }
        ]
    }
    ]
})

router.beforeEach((to, from, next) => {
    var tokenstr = window.sessionStorage.getItem('token')
    if (to.path == '/login' && !tokenstr) return next()
    if (!tokenstr) return next('/login')
    if (to.path == '/login') return next('/home')

    next()
})
export default router
