import {
  users_api,
  usersType_api,
  addUser_api,
  editUser_api,
  delete_api
} from '@/api'
import { log } from 'util'
export default {
  data() {
    // 表单验证规则1
    // 验证邮箱
    var checkEmail = (rule, value, callback) => {
      const regEmail = /^\w+@\w+(\.\w+)+$/
      if (regEmail.test(value)) {
        return callback()
      }
      // 返回错误提示
      callback(new Error('请输入合法的邮箱'))
    }
    //验证手机规则
    var checkMobile = (rule, value, callback) => {
      const regMobile = /^1[34578]\d{9}$/
      if (regMobile.test(value)) {
        return callback()
      }
      // 返回错误提示
      callback(new Error('请输入合法的手机号'))
    }
    return {
      editusersId: '', //编辑的用户id
      addDialogVisible: false,
      editDialogVisible: false,
      input: '',
      tableData: [],
      queryInfo: {
        query: '',
        pagenum: 1,
        pagesize: 2
      },
      total: 0,
      userType: {},
      addForm: {
        username: '',
        password: '',
        email: '',
        mobile: ''
      },
      editForm: {
        username: '',
        email: '',
        mobile: ''
      },
      // 添加表单验证规则2
      addFormRules: {
        // 验证用户名
        username: [
          { required: true, message: '请输入用户名', trigger: 'blur' },
          { min: 3, max: 8, message: '长度在 3 到 8 个字符', trigger: 'blur' }
        ],
        // 验证密码
        password: [
          { required: true, message: '请输入密码', trigger: 'blur' },
          { min: 3, max: 8, message: '长度在 3 到 8 个字符', trigger: 'blur' }
        ],
        //验证邮箱
        email: [
          { required: true, message: '请输入邮箱', trigger: 'blur' },
          {
            validator: checkEmail,
            message: '邮箱格式不正确，请重新输入',
            trigger: 'blur'
          }
        ],
        //  验证手机号
        mobile: [
          { required: true, message: '请输入手机号码', trigger: 'blur' },
          {
            validator: checkMobile,
            message: '手机号码不正确，请重新输入',
            trigger: 'blur'
          }
        ]
      },
      //编辑表单验证规则2
      editFormRules: {
        // 验证用户名
        username: [
          { required: true, message: '请输入用户名', trigger: 'blur' },
          { min: 3, max: 8, message: '长度在 3 到 8 个字符', trigger: 'blur' }
        ],
        //验证邮箱
        email: [
          { required: true, message: '请输入邮箱', trigger: 'blur' },
          {
            validator: checkEmail,
            message: '邮箱格式不正确，请重新输入',
            trigger: 'blur'
          }
        ],
        //  验证手机号
        mobile: [
          { required: true, message: '请输入手机号码', trigger: 'blur' },
          {
            validator: checkMobile,
            message: '手机号码不正确，请重新输入',
            trigger: 'blur'
          }
        ]
      }
    }
  },
  methods: {
    //获取用户列表函数
    async getUsers() {
      //   console.log(this.input)
      this.queryInfo.query = this.input
      const { data: res } = await users_api(this.queryInfo)
      // console.log(this.queryInfo);
      // console.log(res);
      if (res.meta.status != 200)
        return this.$message.error('获取用户数据列表失败')
      this.$message.success('获取用户数据列表成功')
      this.tableData = res.data.users
      this.total = res.data.total
    },
    handleSizeChange(val) {
      // console.log(`每页 ${val} 条`);
      this.queryInfo.pagesize = val
      this.getUsers()
    },
    handleCurrentChange(val) {
      // console.log(`当前页: ${val}`);
      this.queryInfo.pagenum = val
      this.getUsers()
    },
    async userStateChanged(row) {
      //改变状态函数
      const { data: res } = await usersType_api({
        id: row.id,
        type: row.mg_state
      })
    },
    addUser() {
      //弹出添加用户表单
      this.addDialogVisible = true
    },
    addDialogClosed() {
      //关闭添加用户表单时清空状态
      // console.log(this);
      this.$refs.addFormRef.resetFields()
    },
    addUserList() {
      this.$refs.addFormRef.validate(async result => {
        // console.log(result);
        if (!result) return this.$message.error('获取用户数据列表失败')
        // console.log(this.addForm);
        const { data: res } = await addUser_api(this.addForm)
        console.log(res)
        //判断如果添加失败，就做提示
        if (res.meta.status !== 201) return this.$message.error('添加用户失败')
        console.log('添加用户成功')
        //添加成功的提示
        this.$message.success('添加用户成功')
        //关闭对话框
        this.addDialogVisible = false
        //重新请求最新的数据
        this.getUsers()
      })
    },
    editUser(res) {
      //弹出添加用户表单
      //   console.log(res);
      this.editForm = {
        username: res.username,
        email: res.email,
        mobile: res.mobile
      }
      this.editusersId = res.id
      //   console.log(this.editusersId);

      this.editDialogVisible = true
    },
    editDialogClosed() {
      //关闭编辑用户表单时清空状态
      // console.log(this);
      this.$refs.editFormRef.resetFields()
    },
    editUserList() {
      this.$refs.editFormRef.validate(async result => {
        if (!result) return this.$message.error('修改失败')
        const { data: res } = await editUser_api({
          id: this.editusersId,
          email: this.editForm.email,
          mobile: this.editForm.mobile
        })
        // console.log(res);
        //判断如果添加失败，就做提示
        if (res.meta.status !== 200) return this.$message.error('添加用户失败')
        console.log('添加用户成功')
        //添加成功的提示
        this.$message.success('添加用户成功')
        //关闭对话框
        this.editDialogVisible = false
        //重新请求最新的数据
        this.getUsers()
      })
    },
    async open(rest) {
      const confirmResult = await this.$confirm(
        '此操作将永久删除该文件, 是否继续?',
        '提示',
        {
          confirmButtonText: '确定删除',
          cancelButtonText: '取消',
          type: 'warning'
        }
      ).catch(err => err)
      //如果用户点击确认，则confirmResult 为'confirm'
      //如果用户点击取消, 则confirmResult获取的就是catch的错误消息'cancel'
      if (confirmResult != 'confirm') {
        return this.$message.info('已经取消删除')
      }
	//   console.log(confirmResult) //confirm代表确定
	  const {data:res}=await delete_api(rest.id)
	//   console.log(res);
		 if (res.meta.status !== 200) return this.$message.error('删除用户失败')
        //删除成功的提示
        this.$message.success('删除用户成功')
        //重新请求最新的数据
        this.getUsers()
    }
  },
  //   生命周期钩子函数
  created() {
    this.getUsers() //调取获取用户列表函数
  }
}
