import { gitRights_api } from '@/api'
export default {
  data() {
    return {
      rightsList: []
    }
  },
  methods: {
    async getRight() {
      const { data: res } = await gitRights_api('list')
      // console.log(res.data)
      this.rightsList=res.data
    }
  },
  created() {
    this.getRight()
  }
}
